import React from 'react';
import OfferingCard from '../OfferingCard/OfferingCard';
import './Offerings.scss';
import { Controller, Scene } from 'react-scrollmagic';
import { Tween, SplitLetters } from 'react-gsap';
import morning from './train.png';
import emerging from './digital.png';
import retail from './shopping.png';

const Offerings = () => (
  <div className='offeringsWrapper'>
    <Controller>
      <Scene pin={false} reverse={false} duration={500} offset={-200}>
        <Tween
          wrapper={<div className='textContainer' />}
          staggerFrom={{
            left: -2000,
            rotation: -720,
            opacity: 0,
            color: '#ff0000',
            ease: 'Expo.easeOut',
          }}
          stagger={0.15}
        >
          <SplitLetters>
            <span className='text'>Animation & Parallax!</span>
          </SplitLetters>
        </Tween>
      </Scene>
    </Controller>

    <div className='offerings'>
      <OfferingCard
        header='Morning Brew'
        meta='morning'
        icon={morning}
        url='https://www.morningbrew.com/daily'
        cta='Subscribe'
        description='This morning, over 2 million people started their day with the latest news from Wall Street to Silicon Valley.'
      />
      <OfferingCard
        header='Emerging Tech Brew'
        meta='emerging'
        url='https://www.morningbrew.com/emerging-tech'
        icon={emerging}
        cta='Subscribe'
        description='Over 175,000 readers turn to this newsletter for the latest on AI, robotics, self-driving cars, and every other technology shaping the future of business.'
      />
      <OfferingCard
        header='Retail Brew'
        meta='retail'
        url='https://www.morningbrew.com/retail'
        icon={retail}
        cta='Subscribe'
        description='Everything you need to know about everything you buy. Sent three times per week to over 110,000 retail professionals.'
      />
    </div>
  </div>
);

export default Offerings;
