import React from 'react';
import HamburgerMenu from 'react-hamburger-menu';
import './Header.scss';

class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = { open: false };
  }

  handleClick() {
    this.setState({
      open: !this.state.open,
    });
  }

  render() {
    return (
      <div className='header'>
        <a href='/' className='logo'>
          <img
            src='https://www.morningbrew.com/assets/daily-logo-1e8cdd1988913cb9e09a7775b2860ac15393f4c34872ffb9c82d83acae1f1a19.png'
            alt='Morning Brew Logo'
          />
        </a>
        <div className='links'>
          <HamburgerMenu
            className='hamburger'
            isOpen={this.state.open}
            menuClicked={this.handleClick.bind(this)}
            width={18}
            height={15}
            strokeWidth={1}
            rotate={0}
            color='black'
            borderRadius={0}
            animationDuration={0.5}
          />
          <ul>
            <li>
              <a href='/'> Newsletters </a>
            </li>
            <li>
              <a href='/'>Podcasts</a>
            </li>
            <li>
              <a href='/'>Team </a>
            </li>
          </ul>
        </div>
      </div>
    );
  }
}

export default Header;
